import React from 'react'

const DisplayPersons = ({persons, filter, onClick}) => {
    return(
      <div> 
      {persons.map((person,index) => {
        const displayContactFormat = <><div key={person.name}>{person.name} {person.number !== undefined && person.number}</div><button onClick={() =>onClick(person.id)}>delete</button></>;
        return filter===''?displayContactFormat:person.name.toLowerCase().includes(filter
          .toLowerCase()) && displayContactFormat
      })}
      </div>
    )
}

export default DisplayPersons
